mapper.update:
UPDATE "SHARE" SET
BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent},
SHARE_BOARD_CATEGORY_DETAIL_CODE = #{shareBoardCategoryDetailCode}
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

UPDATE "SHARE" SET
BOARD_DEL_FL= 'Y'
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}
