mapper.select:
SELECT COUNT(*) FROM "MEMBER"
WHERE MEMBER_ID = #{memberId}
AND MEMBER_DEL_FL = 'N'

SELECT COUNT(*) FROM "MEMBER"
WHERE MEMBER_EMAIL = #{memberEmail}
AND MEMBER_DEL_FL = 'N'

SELECT COUNT(*) FROM "MEMBER"
WHERE MEMBER_NICKNAME = #{memberNickname}
AND MEMBER_DEL_FL = 'N'

	SELECT MEMBER_ID
  	FROM MEMBER
  	WHERE MEMBER_EMAIL = #{email}

SELECT COUNT(*) FROM "MEMBER"
WHERE MEMBER_ID = #{memberId}
AND MEMBER_EMAIL = #{memberEmail}
AND MEMBER_DEL_FL = 'N'

mapper.update:
UPDATE MEMBER
SET MEMBER_PW = #{memberPw}
WHERE MEMBER_ID = #{memberId}
AND MEMBER_DEL_FL = 'N'

UPDATE MEMBER
SET MEMBER_PW = #{encPw}
WHERE MEMBER_NO = #{memberNo}
AND MEMBER_DEL_FL = 'N'

UPDATE MEMBER
SET MEMBER_DEL_FL = 'Y'
WHERE MEMBER_NO = #{memberNo}
