mapper.delete:
DELETE FROM "JJIM"
WHERE MEMBER_NO = #{memberNo}
AND BOARD_NO = #{boardNo}

mapper.update:
UPDATE "SHARE" SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT B.BOARD_NO, B.BOARD_TITLE, M.MEMBER_NICKNAME, B.READ_COUNT, B.BOARD_CONTENT, B.SHARE_STATUS, (SELECT COUNT(*) FROM "SHARE_COMMENT" C WHERE C.BOARD_NO = B.BOARD_NO) COMMENT_COUNT, (SELECT COUNT(*) FROM "JJIM" J WHERE J.BOARD_NO = B.BOARD_NO) JJIM_COUNT, SCD.SHARE_BOARD_CATEGORY_DETAIL_NAME, SC.SHARE_BOARD_CATEGORY_NAME,
CASE WHEN SYSDATE - B.BOARD_WRITE_DATE < 1/24/60 THEN FLOOR((SYSDATE - B.BOARD_WRITE_DATE)*24*60*60) || '초 전' WHEN SYSDATE - B.BOARD_WRITE_DATE < 1/24 THEN FLOOR((SYSDATE - B.BOARD_WRITE_DATE)*24*60) || '분 전' WHEN SYSDATE - B.BOARD_WRITE_DATE < 1 THEN FLOOR((SYSDATE - B.BOARD_WRITE_DATE)*24) || '시간 전' WHEN SYSDATE - B.BOARD_WRITE_DATE < 30 THEN FLOOR(SYSDATE - B.BOARD_WRITE_DATE) || '일 전' WHEN SYSDATE - B.BOARD_WRITE_DATE < 365 THEN FLOOR((SYSDATE - B.BOARD_WRITE_DATE)/30) || '달 전' ELSE FLOOR((SYSDATE - B.BOARD_WRITE_DATE)/365) || '년 전' END AS BOARD_WRITE_DATE
FROM "SHARE" B JOIN "MEMBER" M ON B.MEMBER_NO = M.MEMBER_NO JOIN "SHARE_CATEGORY_DETAIL" SCD ON B.SHARE_BOARD_CATEGORY_DETAIL_CODE = SCD.SHARE_BOARD_CATEGORY_DETAIL_CODE JOIN "SHARE_CATEGORY" SC ON SCD.SHARE_BOARD_CATEGORY_CODE = SC.SHARE_BOARD_CATEGORY_CODE WHERE B.BOARD_DEL_FL = 'N' AND B.BOARD_CODE = #{boardCode}
AND SC.SHARE_BOARD_CATEGORY_NAME = #{mainCategory}
AND SCD.SHARE_BOARD_CATEGORY_DETAIL_NAME = #{subCategory}
ORDER BY B.BOARD_NO DESC
